INSERT INTO `category` (`id`, `categoryName`, `categoryDescription`, `creationDate`, `updationDate`) VALUES
(8, 'WOMAN', 'Discover a curated collection of stylish and timeless pieces designed for women of all ages. From elegant dresses to comfortable everyday wear each item embodies quality and sophistication. Perfect for work casual outings and special occasions our selection ensures you look and feel your best every day.\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '2024-08-24 03:52:40', '24-08-2024 09:24:06 AM'),
(10, 'MAN', 'Discover our Mens category featuring stylish and high Quality apparel designed for every occasion. From casual wear to formal attire our collection combines comfort with modern trends ensuring you look and feel your best. Explore a range of clothing accessories and footwear tailored to elevate your wardrobe.', '2024-08-24 03:54:37', '24-08-2024 09:24:45 AM'),
(29, 'KIDS', 'Discover our KIDS category featuring a variety of products for your little ones. From clothing to toys every item is chosen to bring joy and comfort to your child. Shop now for high-quality products that meet your kids needs.', '2024-09-13 14:46:27', '17-09-2024 09:06:31 PM');
INSERT INTO `chatbot` (`id`, `queries`, `replies`) VALUES
(3, 'How can I track my order?', 'You can track your order by visiting the \'Track Order\' section on our website and entering your order ID & email ID.'),
(4, 'What payment methods do you accept?', 'We accept net banking(QR CODE) and COD for secure transactions'),
(5, 'How can I return a product?', 'You can request a return within 7 days of delivery by visiting the \'My Orders\' section and selecting the return option.'),
(6, 'Do you offer free shipping?', 'Yes, we offer free shipping on orders above $50. For orders below that, standard shipping charges apply.'),
(7, 'How long does delivery take?', 'Delivery time varies by location. Standard shipping takes 5-7 business days, while express delivery takes 2-3 business days.'),
(8, 'Can I cancel my order after placing it?', 'Yes, you can cancel your order within 24 hours of placing it from the \'My Orders\' section. After that, cancellation may not be possible.'),
(9, 'How can I contact customer support?', 'You can contact our support team via live chat, email at quintetonline@gmail.com, or call our helpline at +1234567890.'),
(10, 'Do you have a refund policy?', 'Yes, refunds are processed within 7-10 business days after the returned item is received and verified.'),
(11, 'Can I change my delivery address after placing an order?', 'You can update your address within 12 hours of placing the order by contacting our support team.'),
(12, 'How do I create an account?', 'You can create an account by clicking on \'Sign Up\' and filling in your details like name, email, and password.'),
(13, 'I forgot my password. How can I reset it?', 'Click on \'Forgot Password\' on the login page and follow the instructions to reset your password.'),
(14, 'Do you offer Cash on Delivery (COD)?', 'Yes, COD is available for select locations. You can check the availability at checkout.'),
(15, 'What should I do if I receive a damaged product?', 'If you receive a damaged or defective product, please contact our support team within 48 hours for a replacement or refund.'),
(16, 'Do you offer gift wrapping services?', 'Yes, you can select the \'Gift Wrap\' option at checkout for an additional charge.'),
(17, 'How can I check if a product is in stock?', 'The product page shows the availability status. If out of stock, you can enable \'Notify Me\' to get updates.'),
(18, 'What happens if my order is delayed?', 'In case of a delay, we will notify you via email or SMS. You can also check the status in \'My Orders\'.'),
(19, 'Can I order multiple items at once?', 'Yes, you can add multiple items to your cart and place a single order for them.'),
(20, 'How can I leave a review for a product?', 'You can leave a review by visiting the product page and clicking on \'Write a Review\' after purchasing the item.'),
(21, 'How can I get updates on new arrivals?', 'Subscribe to our newsletter or follow us on social media for updates on new arrivals and offers.');
